@prefix geojson: <https://purl.org/geojson/vocab#> .
@prefix ns1: <http://example.org/myModel/> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .

<http://example.com/features/f1> a geojson:Feature ;
    ns1:myProp "Mandatory property example" ;
    geojson:geometry [ a geojson:LineString ;
            geojson:coordinates ( ( -1.116718e+02 4.005671e+01 ) ( -1.1171e+02 4.015671e+01 ) ) ] .

transforms.code:
@prefix mymodel: <http://example.org/myModel/> .
@prefix sh: <http://www.w3.org/ns/shacl#>.

<urn:example:LabelToDCTERMS>
    a sh:NodeShape ;
    sh:targetSubjectsOf mymodel:myProp;
    sh:rule [
        a sh:SPARQLRule ;
        sh:construct """
            prefix dcterms: <http://purl.org/dc/terms/>
            prefix mymodel: <http://example.org/myModel/>
            CONSTRUCT {
                $this dcterms:identifier ?myPropUpper
            } WHERE {
                $this mymodel:myProp ?myProp
                BIND(UCASE(?myProp) as ?myPropUpper)
            }
        """
    ] ;
.
